mapper.select:
select CONCAT_WS(' ',a.date,a.app_id) id,number from t_statistical_app_active a join t_app ap on ap.`status`=true and ap.app_id=a.app_id where 1=1
and ap.channel_id=#{channelId}
and a.date>=DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -6 DAY),'%Y-%m-%d') and DATE_FORMAT(NOW(),'%Y-%m-%d')>=a.date

select SUM(a.number) from t_statistical_app_active a join t_app ap on ap.`status`=true and ap.app_id=a.app_id
and ap.channel_id=#{channelId}
where a.date=DATE_FORMAT(DATE_ADD(now(),INTERVAL -1 day),'%Y-%m-%d')
