mapper.update:
update t_user set status=false 
where user_id in
(

	#{item.userId}

)

mapper.select:
select DATE_FORMAT(birthday,'%Y-%m-%d') birthday,u.* from t_user u where u.status=1

	and u.app_id=#{appId}


	and u.username=#{username}


	and u.phone=#{phone}


	and u.email=#{email}


	and u.enable_status=${enableStatus}

select u.user_id, ap.app_name, u.phone, DATE_FORMAT(u.create_time,'%Y-%m-%d %H:%i:%s') create_time, lg.device_id, dv.device_name lastLoginDeviceName, dv.wireless_mac lastLoginDeviceMac, DATE_FORMAT(lg.create_time,'%Y-%m-%d %H:%i:%s') last_login_time from t_user u left join t_app ap on ap.app_id=u.app_id left join( select lg.* from t_user_login lg left join t_user_login lg2 on lg2.user_id=lg.user_id and lg2.create_time>lg.create_time WHERE ISNULL(lg2.user_login_id) ) lg on lg.user_id=u.user_id left join t_device dv on dv.device_id=lg.device_id where 1=1 AND u.`status`=true
and ap.channel_id=#{channelId}
and ( u.user_id like CONCAT('%',#{user},'%') or u.phone like CONCAT('%',#{user},'%') or u.email like CONCAT('%',#{user},'%') )
and u.app_id=#{appId}
and ( dv.device_id like CONCAT('%',#{device},'%') or dv.device_name like CONCAT('%',#{device},'%') or dv.wireless_mac like CONCAT('%',#{device},'%') or dv.uuid like CONCAT('%',#{device},'%') )
and DATE_FORMAT(u.create_time,'%Y%m%d')>=DATE_FORMAT(#{startTime},'%Y%m%d')
and DATE_FORMAT(u.create_time,'%Y%m%d')<=DATE_FORMAT(#{endTime},'%Y%m%d')
and u.enable_status=${enableStatus}
and u.remark like CONCAT('%',#{remark},'%')
ORDER BY u.create_time desc

select * from t_user
phone = #{ownerTel}
order by create_time desc limit 1

mapper.update:
update t_user
username = #{ownerId}, nick_name = #{ownerName}, age = #{ownerAge}, phone = #{ownerTel},
user_id = #{userId}
