mapper.select:
select e.user_id, e.owner_id, e.owner_name, e.owner_tel, e.owner_age, e.owner_height, e.eye_id, u.username, u.phone, e.type, e.height, e.width, e.pupi_distance, e.length_of_optic_axis, e.corneal_diameter, e.corneal_thickness, e.pupil_diameter, e.intraocular_pressure, e.radius_curvature, e.refractive_index_for_eyeball, e.radius_curvature_for_retina, e.aperture_for_high_light, e.aperture_for_low_light, e.focus_distance_for_object_space, e.focus_distance_for_image_space, e.focal_power_for_image_space, e.eye_id, e.create_time, e.update_time, e.remark
from t_eye e left join t_user u on e.user_id=u.user_id where 1=1
and e.eye_id=#{eyeId}
and u.user_id=#{userId}
and e.`status`=true group by e.eye_id limit 1

select e.user_id, e.owner_id, e.owner_name, e.owner_tel, e.owner_age, e.owner_height, e.eye_id, u.username, u.phone, e.type, e.height, e.width, e.pupi_distance, e.length_of_optic_axis, e.corneal_diameter, e.corneal_thickness, e.pupil_diameter, e.intraocular_pressure, e.radius_curvature, e.refractive_index_for_eyeball, e.radius_curvature_for_retina, e.aperture_for_high_light, e.aperture_for_low_light, e.focus_distance_for_object_space, e.focus_distance_for_image_space, e.focal_power_for_image_space, e.create_time, e.update_time, e.remark
from t_eye e left join t_user u on e.user_id=u.user_id where 1=1
and u.user_id=#{userId}
and e.type=#{type}
and e.`status`=true group by e.eye_id

mapper.update:
update t_eye set
	status=false
where eye_id in (

	#{item.eyeId}

)
