mapper.select:
select vt.eye_id, vt.date_time, vt.device_type, vt.distance, vt.env_light_intensity, vt.time_duration, vt.test_mode, vt.test_id, vt.create_time, vt.update_time, vt.remark from t_vision_test vt where 1=1
and vt.test_id=#{testId}
and vt.`status`=true group by vt.test_id limit 1000

select vt.eye_id, vt.date_time, vt.device_type, vt.distance, vt.env_light_intensity, vt.time_duration, vt.test_mode, vt.test_id, vt.create_time, vt.update_time, vt.remark from t_vision_test vt where 1=1
and vt.eye_id=#{eyeId}
and vt.test_id=#{testId}
and vt.date_time >= #{beginTime}
and vt.date_time <= #{endTime}
and vt.device_type=#{deviceType}
and vt.distance=#{distance}
and vt.`status`=true group by vt.test_id

mapper.update:
update t_vision_test set
	status=false
where test_id in (

	#{item.testId}

)
